/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 210 4% 98%;

  --foreground: 210 6% 12%;

  --border: 210 4% 88%;

  --card: 210 4% 96%;

  --card-foreground: 210 6% 14%;

  --card-border: 210 4% 90%;

  --sidebar: 210 4% 94%;

  --sidebar-foreground: 210 6% 16%;

  --sidebar-border: 210 4% 86%;

  --sidebar-primary: 250 75% 55%;

  --sidebar-primary-foreground: 250 75% 98%;

  --sidebar-accent: 210 6% 88%;

  --sidebar-accent-foreground: 210 6% 18%;

  --sidebar-ring: 250 75% 55%;

  --popover: 210 4% 92%;

  --popover-foreground: 210 6% 16%;

  --popover-border: 210 4% 84%;

  --primary: 250 75% 55%;

  --primary-foreground: 250 75% 98%;

  --secondary: 210 6% 86%;

  --secondary-foreground: 210 6% 18%;

  --muted: 210 8% 90%;

  --muted-foreground: 210 6% 32%;

  --accent: 210 10% 88%;

  --accent-foreground: 210 8% 20%;

  --destructive: 0 84% 42%;

  --destructive-foreground: 0 84% 98%;

  --input: 210 10% 68%;
  --ring: 250 75% 55%;
  --chart-1: 250 75% 50%;
  --chart-2: 195 75% 38%;
  --chart-3: 160 70% 35%;
  --chart-4: 280 65% 45%;
  --chart-5: 340 75% 48%;

  --font-sans: Inter, Work Sans, Noto Sans TC, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: JetBrains Mono, Menlo, monospace;
  --radius: .75rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(210 10% 10% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(210 10% 10% / 0.08);
  --shadow-sm: 0px 2px 4px -1px hsl(210 10% 10% / 0.06), 0px 1px 2px -1px hsl(210 10% 10% / 0.04);
  --shadow: 0px 4px 6px -1px hsl(210 10% 10% / 0.08), 0px 2px 4px -2px hsl(210 10% 10% / 0.05);
  --shadow-md: 0px 6px 12px -2px hsl(210 10% 10% / 0.10), 0px 3px 6px -3px hsl(210 10% 10% / 0.06);
  --shadow-lg: 0px 10px 20px -3px hsl(210 10% 10% / 0.12), 0px 4px 8px -4px hsl(210 10% 10% / 0.08);
  --shadow-xl: 0px 20px 30px -5px hsl(210 10% 10% / 0.14), 0px 8px 12px -6px hsl(210 10% 10% / 0.10);
  --shadow-2xl: 0px 25px 40px -8px hsl(210 10% 10% / 0.18);
  --tracking-normal: -0.01em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

