  --shadow-sm: 0px 2px 4px -1px hsl(210 10% 0% / 0.25), 0px 1px 2px -1px hsl(210 10% 0% / 0.20);
  --shadow: 0px 4px 6px -1px hsl(210 10% 0% / 0.35), 0px 2px 4px -2px hsl(210 10% 0% / 0.25);
  --shadow-md: 0px 6px 12px -2px hsl(210 10% 0% / 0.40), 0px 3px 6px -3px hsl(210 10% 0% / 0.30);
