import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { type MindMapNode, type WordCategory } from "@shared/schema";
import { CategoryButtons } from "@/components/CategoryButtons";
import { MindMapCanvas } from "@/components/MindMapCanvas";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/contexts/LanguageContext";
import { useTheme } from "@/contexts/ThemeContext";
import { useTranslation } from "@/lib/i18n";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Play, Loader2, Undo2, Redo2, Save, Download } from "lucide-react";
import html2canvas from "html2canvas";
import { getCategoryColor, getCategoryLabel } from "@shared/categoryColors";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

export default function Home() {
  // Check if we're editing an existing mind map
  const [, params] = useRoute("/mindmap/:id");
  const mindMapId = params?.id !== "new" ? params?.id : undefined;

  const [nodes, setNodes] = useState<MindMapNode[]>([]);
  const [centerNodeId, setCenterNodeId] = useState<string | undefined>();
  const [initialWord, setInitialWord] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);

  // Get word from URL query parameter if creating new mind map
  useEffect(() => {
    if (!mindMapId) {
      const urlParams = new URLSearchParams(window.location.search);
      const wordParam = urlParams.get('word');
      if (wordParam) {
        setInitialWord(wordParam);
        // Auto-start with the word
        setTimeout(() => {
          const newNode: MindMapNode = {
            id: crypto.randomUUID(),
            word: wordParam.trim(),
            x: 0,
            y: 0,
            isCenter: true,
          };
          setNodes([newNode]);
          setCenterNodeId(newNode.id);
          setHistory([[newNode]]);
          setHistoryIndex(0);
        }, 100);
      }
    }
  }, [mindMapId]);

  // Undo/Redo history stacks (initialize with empty state)
  const [history, setHistory] = useState<MindMapNode[][]>([[]]);
  const [historyIndex, setHistoryIndex] = useState(0);
  const [isSaved, setIsSaved] = useState(false);
  const [showSaveConfirmDialog, setShowSaveConfirmDialog] = useState(false);

  const { toast } = useToast();
  const { language } = useLanguage();
  const { theme } = useTheme();
  const t = useTranslation(language);

  // Load existing mind map if editing
  const { data: existingMindMap, isLoading: isLoadingMindMap } = useQuery({
    queryKey: ["/api/mindmaps", mindMapId],
    queryFn: async () => {
      if (!mindMapId) return null;
      const response = await fetch(`/api/mindmaps/${mindMapId}`, {
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error("Failed to load mind map");
      }
      return await response.json();
    },
    enabled: !!mindMapId,
  });

  // Initialize with existing mind map data
  useEffect(() => {
    if (existingMindMap?.nodes && Array.isArray(existingMindMap.nodes)) {
      const loadedNodes = existingMindMap.nodes as MindMapNode[];
      setNodes(loadedNodes);
      const centerNode = loadedNodes.find((n) => n.isCenter);
      if (centerNode) {
        setCenterNodeId(centerNode.id);
      }
      // Initialize history with loaded state
      setHistory([loadedNodes]);
      setHistoryIndex(0);
      setIsSaved(true);
    }
  }, [existingMindMap]);

  // Wrapper to update nodes with history tracking
  const updateNodes = (newNodes: MindMapNode[] | ((prev: MindMapNode[]) => MindMapNode[])) => {
    setNodes((prevNodes) => {
      const nextNodes = typeof newNodes === 'function' ? newNodes(prevNodes) : newNodes;

      // Add to history (remove any forward history if we're not at the end)
      setHistory((prevHistory) => {
        const newHistory = prevHistory.slice(0, historyIndex + 1);
        return [...newHistory, nextNodes];
      });
      setHistoryIndex((prev) => prev + 1);
      setIsSaved(false);

      return nextNodes;
    });
  };

  // Undo function
  const handleUndo = () => {
    if (historyIndex > 0) {
      const prevState = history[historyIndex - 1];
      setHistoryIndex(historyIndex - 1);
      setNodes(prevState);
      toast({
        title: language === "en" ? "Undo" : "復原",
        description: language === "en" ? "Action undone" : "已復原上一步",

      });
    }
  };

  // Redo function
  const handleRedo = () => {
    if (historyIndex < history.length - 1) {
      const nextState = history[historyIndex + 1];
      setHistoryIndex(historyIndex + 1);
      setNodes(nextState);
      toast({
        title: language === "en" ? "Redo" : "取消復原",
        description: language === "en" ? "Action redone" : "已取消復原",

      });
    }
  };

  // Assign angles to categories (like spokes on a wheel)
  const categories: WordCategory[] = [
    "derivatives",
    "synonyms",
    "antonyms",
    "collocations",
    "idioms",
    "root",
    "prefix",
    "suffix",
    "topic-related",
  ];

  const getCategoryAngle = (category: WordCategory): number => {
    const categoryIndex = categories.indexOf(category);
    return (categoryIndex * 2 * Math.PI) / categories.length;
  };

  // Start with initial word
  const handleStartLearning = () => {
    if (!initialWord.trim()) return;

    // Use fixed coordinates - the canvas will center it
    const newNode: MindMapNode = {
      id: crypto.randomUUID(),
      word: initialWord.trim(),
      x: 0,
      y: 0,
      isCenter: true,
    };

    updateNodes([newNode]);
    setCenterNodeId(newNode.id);
    setInitialWord("");
  };

  // Reset map to start fresh
  const handleResetMap = () => {
    updateNodes([]);
    setCenterNodeId(undefined);
    // Clear undo/redo history after reset
    setHistory([[]]);
    setHistoryIndex(0);
    toast({
      title: language === "en" ? "Map cleared" : "心智圖已清空",
      description: language === "en" ? "Start fresh with a new word" : "開始輸入新單字",

    });
  };

  // Generate related words for a category
  const generateWordsMutation = useMutation({
    mutationFn: async ({
      word,
      category,
      targetNodeId,
    }: {
      word: string;
      category: WordCategory;
      targetNodeId: string;
    }) => {
      const response = await apiRequest(
        "POST",
        "/api/generate-words",
        { word, category }
      );
      const data = await response.json();
      return data as { words: string[] };
    },
    onSuccess: (data, variables) => {
      if (!data || !data.words || !Array.isArray(data.words)) {
        console.error("Invalid response format:", data);
        setIsGenerating(false);
        toast({
          title: language === "en" ? "Error" : "錯誤",
          description:
            language === "en"
              ? "Invalid response from server"
              : "伺服器回應格式錯誤",
          variant: "destructive",

        });
        return;
      }

      if (data.words.length === 0) {
        console.warn("No words generated for category:", variables.category);
        setIsGenerating(false);
        toast({
          title: language === "en" ? "No words generated" : "未生成任何單字",
          description:
            language === "en"
              ? `Could not generate ${variables.category} words. Please try another category.`
              : `無法生成${t.categories[variables.category]}單字，請嘗試其他類別。`,
          variant: "destructive",

        });
        return;
      }

      updateNodes((prevNodes) => {
        const centerNode = prevNodes.find((n) => n.id === variables.targetNodeId);
        if (!centerNode) return prevNodes;

        // Get the angle for this category (spider thread direction)
        const angle = getCategoryAngle(variables.category);

        // Base distance from center for first word
        const baseDistance = 250;
        // Fixed gap between node boundaries (edge to edge)
        const boundaryGap = 80;

        // Estimate node width based on text length
        // Approximate: 12px per character + 32px padding (px-4 = 16px each side)
        const estimateNodeWidth = (text: string) => {
          const charWidth = 12;
          const padding = 32;
          const minWidth = 100; // min-w-[100px]
          return Math.max(minWidth, text.length * charWidth + padding);
        };

        let currentDistance = baseDistance;
        const newNodes: MindMapNode[] = [];

        for (let i = 0; i < data.words.length; i++) {
          const word = data.words[i];
          const nodeWidth = estimateNodeWidth(word);

          const node = {
            id: crypto.randomUUID(),
            word,
            x: centerNode.x + currentDistance * Math.cos(angle),
            y: centerNode.y + currentDistance * Math.sin(angle),
            parentId: centerNode.id,
            category: variables.category,
            isCenter: false,
          };

          newNodes.push(node);

          // Calculate next position: 
          // current center + half of current node width + gap + half of next node width
          if (i < data.words.length - 1) {
            const nextWord = data.words[i + 1];
            const nextNodeWidth = estimateNodeWidth(nextWord);
            currentDistance += nodeWidth / 2 + boundaryGap + nextNodeWidth / 2;
          }
        }

        return [...prevNodes, ...newNodes];
      });

      setIsGenerating(false);

      toast({
        title: language === "en" ? "Words generated!" : "單字已生成！",
        description:
          language === "en"
            ? `Added ${data.words.length} ${variables.category} words`
            : `已新增 ${data.words.length} 個${t.categories[variables.category]}`,

      });
    },
    onError: () => {
      setIsGenerating(false);
      toast({
        title: language === "en" ? "Error" : "錯誤",
        description:
          language === "en"
            ? "Failed to generate words. Please try again."
            : "生成單字失敗，請重試。",
        variant: "destructive",

      });
    },
  });

  const handleCategorySelect = (category: WordCategory) => {
    const centerNode = nodes.find((n) => n.id === centerNodeId);
    if (!centerNode) return;

    setIsGenerating(true);
    generateWordsMutation.mutate({ 
      word: centerNode.word, 
      category,
      targetNodeId: centerNode.id
    });
  };

  const handleNodeClick = (nodeId: string) => {
    setCenterNodeId(nodeId);
  };

  // Delete a node and reposition remaining nodes in the same category
  const handleDeleteNode = (nodeId: string) => {
    updateNodes((prevNodes) => {
      const nodeToDelete = prevNodes.find(n => n.id === nodeId);
      if (!nodeToDelete || nodeToDelete.isCenter) return prevNodes; // Cannot delete center node

      // Get all nodes in the same category with the same parent
      const sameCategory = prevNodes.filter(
        n => n.category === nodeToDelete.category && n.parentId === nodeToDelete.parentId
      );

      // Remove the node
      const nodesAfterDelete = prevNodes.filter(n => n.id !== nodeId);

      // If there are other nodes in the same category, reposition them
      if (sameCategory.length > 1) {
        const centerNode = prevNodes.find(n => n.id === nodeToDelete.parentId);
        if (!centerNode) return nodesAfterDelete;

        // Get remaining nodes in this category (sorted by distance)
        const remainingInCategory = sameCategory
          .filter(n => n.id !== nodeId)
          .sort((a, b) => {
            const distA = Math.sqrt(Math.pow(a.x - centerNode.x, 2) + Math.pow(a.y - centerNode.y, 2));
            const distB = Math.sqrt(Math.pow(b.x - centerNode.x, 2) + Math.pow(b.y - centerNode.y, 2));
            return distA - distB;
          });

        // Recalculate positions
        if (!nodeToDelete.category) return nodesAfterDelete;
        const angle = getCategoryAngle(nodeToDelete.category);
        const baseDistance = 250;
        const boundaryGap = 80;

        const estimateNodeWidth = (text: string) => {
          const charWidth = 12;
          const padding = 32;
          const minWidth = 100;
          return Math.max(minWidth, text.length * charWidth + padding);
        };

        let currentDistance = baseDistance;

        return nodesAfterDelete.map(node => {
          const indexInCategory = remainingInCategory.findIndex(n => n.id === node.id);

          if (indexInCategory >= 0) {
            // Recalculate position for this node
            const nodeWidth = estimateNodeWidth(node.word);

            const updatedNode = {
              ...node,
              x: centerNode.x + currentDistance * Math.cos(angle),
              y: centerNode.y + currentDistance * Math.sin(angle),
            };

            // Calculate distance for next node
            if (indexInCategory < remainingInCategory.length - 1) {
              const nextNode = remainingInCategory[indexInCategory + 1];
              const nextNodeWidth = estimateNodeWidth(nextNode.word);
              currentDistance += nodeWidth / 2 + boundaryGap + nextNodeWidth / 2;
            }

            return updatedNode;
          }

          return node;
        });
      }

      return nodesAfterDelete;
    });

    toast({
      title: language === "en" ? "Node deleted" : "節點已刪除",
      description: language === "en" ? "Node removed successfully" : "節點已成功移除",

    });
  };

  // Save mind map mutation
  const saveMindMapMutation = useMutation({
    mutationFn: async () => {
      const centerNode = nodes.find(n => n.isCenter);
      const name = centerNode ? centerNode.word : "Untitled Mind Map";

      // Update existing mind map or create new one
      if (mindMapId) {
        const response = await apiRequest("PATCH", `/api/mindmaps/${mindMapId}`, {
          name,
          nodes,
        });
        return await response.json();
      } else {
        const response = await apiRequest("POST", "/api/mindmaps", {
          name,
          nodes,
        });
        return await response.json();
      }
    },
    onSuccess: () => {
      setIsSaved(true);
      // Clear history after save (keep current state only)
      setHistory([nodes]);
      setHistoryIndex(0);

      // Invalidate mind maps query to refresh the list
      queryClient.invalidateQueries({ queryKey: ["/api/mindmaps"] });

      // Show confirmation dialog to ask about saving as flashcards
      setShowSaveConfirmDialog(true);
    },
    onError: () => {
      toast({
        title: language === "en" ? "Save failed" : "儲存失敗",
        description: language === "en" ? "Failed to save mind map" : "儲存心智圖失敗",
        variant: "destructive",

      });
    },
  });

  const handleSave = () => {
    if (nodes.length === 0) return;
    saveMindMapMutation.mutate();
  };

  // Create flashcards from mind map mutation
  const createFlashcardsMutation = useMutation({
    mutationFn: async () => {
      const centerNode = nodes.find(n => n.isCenter);
      const deckName = centerNode ? `${centerNode.word} - Mind Map` : "Mind Map Flashcards";

      // Get all unique words from the mind map (excluding center node)
      const words = nodes
        .filter(n => !n.isCenter && n.word)
        .map(n => n.word.trim())
        .filter((word, index, self) => self.indexOf(word) === index); // Remove duplicates

      if (words.length === 0) {
        throw new Error(language === "en" ? "No words to create flashcards" : "沒有單字可以建立字卡");
      }

      const response = await apiRequest("POST", "/api/flashcards/batch-create", { 
        name: deckName, 
        words 
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ message: "Failed to create deck" }));
        throw new Error(errorData.message || "Failed to create deck");
      }

      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flashcards"] });
      toast({
        title: language === "en" ? "Flashcards created" : "字卡已建立",
        description: language === "en" ? "Flashcard deck created successfully" : "字卡組已成功建立",

      });
      setShowSaveConfirmDialog(false);
    },
    onError: (error: Error) => {
      toast({
        title: language === "en" ? "Error" : "錯誤",
        description: error.message || (language === "en" ? "Failed to create flashcards" : "建立字卡失敗"),
        variant: "destructive",
        duration: 5000,
      });
      setShowSaveConfirmDialog(false);
    },
  });

  const handleCreateFlashcards = () => {
    createFlashcardsMutation.mutate();
  };

  // Download as PNG function - captures all nodes in the mind map
  const handleDownloadPNG = async () => {
    if (nodes.length === 0) {
      toast({
        title: language === "en" ? "Export failed" : "匯出失敗",
        description: language === "en" ? "No nodes to export" : "沒有節點可匯出",
        variant: "destructive",

      });
      return;
    }

    try {
      // Calculate bounding box of all nodes
      const padding = 100; // Extra padding around nodes
      let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;

      nodes.forEach(node => {
        const nodeWidth = 120; // Approximate node width
        const nodeHeight = 60; // Approximate node height
        minX = Math.min(minX, node.x - nodeWidth / 2);
        minY = Math.min(minY, node.y - nodeHeight / 2);
        maxX = Math.max(maxX, node.x + nodeWidth / 2);
        maxY = Math.max(maxY, node.y + nodeHeight / 2);
      });

      const width = maxX - minX + padding * 2;
      const height = maxY - minY + padding * 2;
      const backgroundColor = theme === 'dark' ? '#14151a' : '#ffffff';

      // Create a temporary container with all nodes positioned correctly
      const tempContainer = document.createElement('div');
      tempContainer.style.position = 'fixed';
      tempContainer.style.top = '-99999px';
      tempContainer.style.left = '-99999px';
      tempContainer.style.width = `${width}px`;
      tempContainer.style.height = `${height}px`;
      tempContainer.style.backgroundColor = backgroundColor;
      document.body.appendChild(tempContainer);

      // Clone the canvas content
      const canvasElement = document.querySelector('[data-mindmap-canvas]') as HTMLElement;
      if (!canvasElement) {
        document.body.removeChild(tempContainer);
        toast({
          title: language === "en" ? "Export failed" : "匯出失敗",
          description: language === "en" ? "Canvas not found" : "找不到畫布",
          variant: "destructive",

        });
        return;
      }

      // Create SVG for connections
      const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
      svg.setAttribute('width', width.toString());
      svg.setAttribute('height', height.toString());
      svg.style.position = 'absolute';
      svg.style.top = '0';
      svg.style.left = '0';

      // Draw connection lines
      const centerNode = nodes.find(n => n.isCenter);
      if (centerNode) {
        const categoryThreads = nodes.reduce((acc, node) => {
          if (node.category && node.parentId === centerNode.id) {
            if (!acc[node.category]) {
              acc[node.category] = [];
            }
            acc[node.category].push(node);
          }
          return acc;
        }, {} as Record<string, typeof nodes>);

        Object.entries(categoryThreads).forEach(([category, categoryNodes]) => {
          if (categoryNodes.length === 0) return;
          const sortedNodes = [...categoryNodes].sort((a, b) => {
            const distA = Math.sqrt(Math.pow(a.x - centerNode.x, 2) + Math.pow(a.y - centerNode.y, 2));
            const distB = Math.sqrt(Math.pow(b.x - centerNode.x, 2) + Math.pow(b.y - centerNode.y, 2));
            return distB - distA;
          });
          const furthestNode = sortedNodes[0];
          const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
          line.setAttribute('x1', (centerNode.x - minX + padding).toString());
          line.setAttribute('y1', (centerNode.y - minY + padding).toString());
          line.setAttribute('x2', (furthestNode.x - minX + padding).toString());
          line.setAttribute('y2', (furthestNode.y - minY + padding).toString());
          line.setAttribute('stroke', getCategoryColor(category as any, theme === 'dark'));
          line.setAttribute('stroke-width', '1.5');
          line.setAttribute('opacity', '0.6');
          svg.appendChild(line);
        });
      }

      tempContainer.appendChild(svg);

      // Add nodes
      nodes.forEach(node => {
        const nodeDiv = document.createElement('div');
        nodeDiv.style.position = 'absolute';
        nodeDiv.style.left = `${node.x - minX + padding}px`;
        nodeDiv.style.top = `${node.y - minY + padding}px`;
        nodeDiv.style.transform = 'translate(-50%, -50%)';

        const isCenter = node.id === centerNode?.id;
        const categoryColor = node.category ? getCategoryColor(node.category, theme === 'dark') : undefined;

        const contentDiv = document.createElement('div');
        contentDiv.style.borderRadius = '12px';
        contentDiv.style.fontWeight = '600';
        contentDiv.style.boxShadow = '0 4px 6px -1px rgba(0, 0, 0, 0.1)';
        contentDiv.style.border = '2px solid';
        contentDiv.style.padding = '10px 16px';
        contentDiv.style.fontSize = '18px';
        contentDiv.style.minWidth = '100px';
        contentDiv.style.textAlign = 'center';

        if (isCenter) {
          contentDiv.style.backgroundColor = theme === 'dark' ? 'hsl(250, 75%, 50%)' : 'hsl(250, 75%, 60%)';
          contentDiv.style.color = '#ffffff';
          contentDiv.style.borderColor = theme === 'dark' ? 'hsl(250, 75%, 40%)' : 'hsl(250, 75%, 50%)';
        } else {
          contentDiv.style.backgroundColor = theme === 'dark' ? 'hsl(240, 10%, 10%)' : '#ffffff';
          contentDiv.style.color = categoryColor || (theme === 'dark' ? '#ffffff' : '#000000');
          contentDiv.style.borderColor = categoryColor || (theme === 'dark' ? 'hsl(240, 10%, 20%)' : 'hsl(240, 5%, 90%)');
        }

        const wordSpan = document.createElement('div');
        wordSpan.textContent = node.word;
        wordSpan.style.whiteSpace = 'nowrap';
        contentDiv.appendChild(wordSpan);

        if (node.category && !isCenter) {
          const categorySpan = document.createElement('div');
          categorySpan.textContent = getCategoryLabel(node.category, language);
          categorySpan.style.fontSize = '12px';
          categorySpan.style.marginTop = '4px';
          categorySpan.style.opacity = '0.7';
          contentDiv.appendChild(categorySpan);
        }

        nodeDiv.appendChild(contentDiv);
        tempContainer.appendChild(nodeDiv);
      });

      // Capture with html2canvas
      const canvas = await html2canvas(tempContainer, {
        backgroundColor,
        scale: 2,
        logging: false,
        useCORS: true,
        allowTaint: true,
      });

      // Clean up
      document.body.removeChild(tempContainer);

      // Download
      const link = document.createElement('a');
      const filename = centerNode ? `${centerNode.word}-mindmap.png` : 'mindmap.png';

      link.download = filename;
      link.href = canvas.toDataURL('image/png');
      link.click();

      toast({
        title: language === "en" ? "Downloaded" : "已下載",
        description: language === "en" ? "Mind map exported as PNG" : "心智圖已匯出為 PNG",

      });
    } catch (error) {
      console.error('Export error:', error);
      toast({
        title: language === "en" ? "Export failed" : "匯出失敗",
        description: language === "en" ? "Failed to export mind map" : "匯出心智圖失敗",
        variant: "destructive",

      });
    }
  };

  return (
    <div className="flex flex-col h-full">
      {/* Mind Map Editor */}
      <>
          {/* Mind Map Control Bar - always show for undo/redo access */}
          {(nodes.length > 0 || history.length > 1) && (
            <div className="border-b px-6 py-3 flex items-center justify-between bg-card/50">
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="icon"
                  onClick={handleUndo}
                  disabled={historyIndex <= 0}
                  data-testid="button-undo"
                >
                  <Undo2 className="h-4 w-4" />
                </Button>
                <Button
                  variant="outline"
                  size="icon"
                  onClick={handleRedo}
                  disabled={historyIndex >= history.length - 1}
                  data-testid="button-redo"
                >
                  <Redo2 className="h-4 w-4" />
                </Button>
              </div>

              <div className="flex items-center gap-2">
                <Button
                  variant="default"
                  size="icon"
                  onClick={handleSave}
                  disabled={nodes.length === 0 || saveMindMapMutation.isPending}
                  data-testid="button-save"
                >
                  {saveMindMapMutation.isPending ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Save className="h-4 w-4" />
                  )}
                </Button>
                <Button
                  variant="outline"
                  size="icon"
                  onClick={handleDownloadPNG}
                  disabled={nodes.length === 0}
                  data-testid="button-download"
                >
                  <Download className="h-4 w-4" />
                </Button>
              </div>
            </div>
          )}

          {nodes.length === 0 ? (
            <div className="flex-1 flex items-center justify-center p-8">
              <div className="w-full max-w-md space-y-6 text-center">
                <div>
                  <h2 className="text-4xl font-bold mb-3">{t.appName}</h2>
                  <p className="text-xl text-muted-foreground">{t.tagline}</p>
                </div>

                <div className="flex gap-2">
                  <Input
                    placeholder={t.enterWord}
                    value={initialWord}
                    onChange={(e) => setInitialWord(e.target.value)}
                    onKeyDown={(e) => {
                      if (e.key === "Enter") handleStartLearning();
                    }}
                    data-testid="input-initial-word"
                    className="text-lg"
                  />
                  <Button
                    onClick={handleStartLearning}
                    disabled={!initialWord.trim()}
                    size="lg"
                    data-testid="button-start-learning"
                  >
                    <Play className="h-5 w-5 mr-2" />
                    {t.startLearning}
                  </Button>
                </div>

                <p className="text-sm text-muted-foreground">{t.emptyMap}</p>
              </div>
            </div>
          ) : (
            <>
              <CategoryButtons
                onSelectCategory={handleCategorySelect}
                disabled={!centerNodeId}
                loading={isGenerating}
              />

              <div className="flex-1 relative">
                {isGenerating && (
                  <div className="absolute inset-0 z-50 flex items-center justify-center bg-background/80 backdrop-blur-sm">
                    <div className="flex flex-col items-center gap-3">
                      <Loader2 className="h-12 w-12 animate-spin text-primary" />
                      <p className="text-lg font-medium">{t.generating}</p>
                    </div>
                  </div>
                )}

                <MindMapCanvas
                  nodes={nodes}
                  onNodeClick={handleNodeClick}
                  onNodeDelete={handleDeleteNode}
                  centerNodeId={centerNodeId}
                />
              </div>

            </>
          )}
      </>

      {/* Save confirmation dialog */}
      <AlertDialog open={showSaveConfirmDialog} onOpenChange={setShowSaveConfirmDialog}>
        <AlertDialogContent data-testid="dialog-save-confirm">
          <AlertDialogHeader>
            <AlertDialogTitle>
              {language === "en" ? "Saved successfully!" : "儲存成功！"}
            </AlertDialogTitle>
            <AlertDialogDescription>
              {language === "en" 
                ? "Your mind map has been saved. Would you also like to save these words as flashcards?" 
                : "您的心智圖已儲存。是否也要將這些單字儲存為字卡？"}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-flashcards">
              {language === "en" ? "No, thanks" : "不用了"}
            </AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleCreateFlashcards}
              disabled={createFlashcardsMutation.isPending}
              data-testid="button-confirm-flashcards"
            >
              {createFlashcardsMutation.isPending ? (
                <>
                  <Loader2 className="h-4 w-4 animate-spin mr-2" />
                  {language === "en" ? "Creating..." : "建立中..."}
                </>
              ) : (
                language === "en" ? "Yes, create flashcards" : "是的，建立字卡"
              )}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

