import { useRoute, useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { FlashcardView } from "@/components/FlashcardView";
import { Button } from "@/components/ui/button";
import { ArrowLeft, Loader2 } from "lucide-react";
import { useLanguage } from "@/contexts/LanguageContext";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function FlashcardPractice() {
  const [, params] = useRoute("/flashcards/:id");
  const deckId = params?.id;
  const [, setLocation] = useLocation();
  const { language } = useLanguage();
  const { toast } = useToast();

  // Fetch flashcard deck
  const { data: deck, isLoading } = useQuery({
    queryKey: ["/api/flashcards", deckId],
    queryFn: async () => {
      const response = await fetch(`/api/flashcards/${deckId}`, {
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error("Failed to load flashcard deck");
      }
      return await response.json();
    },
    enabled: !!deckId,
  });

  // Update card mutation
  const updateCardMutation = useMutation({
    mutationFn: async ({ cardId, known }: { cardId: string; known: boolean }) => {
      return await apiRequest("PATCH", `/api/flashcards/${deckId}/cards/${cardId}`, { known });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flashcards", deckId] });
    },
  });

  const handleUpdateCard = (cardId: string, known: boolean) => {
    updateCardMutation.mutate({ cardId, known });
  };

  const handleComplete = () => {
    toast({
      title: language === "en" ? "Practice complete!" : "練習完成！",
      description: language === "en" ? "Great job! Keep up the good work." : "做得好！繼續保持。",
    });
    setLocation("/flashcards");
  };

  const handleBack = () => {
    setLocation("/flashcards");
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
      </div>
    );
  }

  if (!deck || !deck.cards || deck.cards.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center h-full p-6 gap-4">
        <p className="text-lg text-muted-foreground">
          {language === "en" ? "No cards in this deck" : "此字卡組沒有卡片"}
        </p>
        <Button onClick={handleBack} data-testid="button-back">
          <ArrowLeft className="h-4 w-4 mr-2" />
          {language === "en" ? "Back to Flashcards" : "返回字卡"}
        </Button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header */}
      <div className="border-b px-6 py-4 flex items-center gap-4">
        <Button
          variant="ghost"
          size="icon"
          onClick={handleBack}
          data-testid="button-back-header"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <div className="flex items-center gap-3">
          <h1 className="text-2xl font-bold text-primary">Wevro</h1>
          <div className="h-6 w-px bg-border" />
          <h2 className="text-2xl font-semibold">{deck.name}</h2>
        </div>
      </div>

      {/* Flashcard View */}
      <FlashcardView
        cards={deck.cards}
        onUpdateCard={handleUpdateCard}
        onComplete={handleComplete}
      />
    </div>
  );
}

server/dictionary-api.ts.content:
/**
 * Free Dictionary API Adapter
 * 
 * This adapter normalizes responses from dictionaryapi.dev into our standard WordEntry format.
 * The API is free, requires no authentication, and provides:
 * - Phonetics (IPA + audio)
 * - Multiple meanings per word
 * - Definitions, examples, synonyms, antonyms
 * - Etymology
 */

import { type WordEntry, type WordSense, type ProviderInfo } from "@shared/schema";

// Free Dictionary API Response Types
interface DictionaryAPIPhonetic {
  text?: string;
  audio?: string;
}

interface DictionaryAPIDefinition {
  definition: string;
  example?: string;
  synonyms?: string[];
  antonyms?: string[];
}

interface DictionaryAPIMeaning {
  partOfSpeech: string;
  definitions: DictionaryAPIDefinition[];
  synonyms?: string[];
  antonyms?: string[];
}

interface DictionaryAPIResponse {
  word: string;
  phonetic?: string;
  phonetics?: DictionaryAPIPhonetic[];
  origin?: string;
  meanings: DictionaryAPIMeaning[];
}

/**
 * Normalize lemma for consistent cache keys
 * - Convert to lowercase
 * - Trim whitespace
 * - Remove punctuation (except hyphens and apostrophes)
 */
export function normalizeLemma(word: string): string {
  return word
    .toLowerCase()
    .trim()
    .replace(/[^\w\s'-]/g, "")  // Keep letters, numbers, spaces, hyphens, apostrophes
    .replace(/\s+/g, " ");       // Normalize multiple spaces to single space
}

/**
 * Fetch word data from Free Dictionary API
 * API endpoint: https://api.dictionaryapi.dev/api/v2/entries/en/{word}
 */
export async function fetchFromDictionaryAPI(word: string): Promise<WordEntry | null> {
  const normalizedWord = normalizeLemma(word);

  try {
    const response = await fetch(
      `https://api.dictionaryapi.dev/api/v2/entries/en/${encodeURIComponent(normalizedWord)}`,
      {
        headers: {
          "Accept": "application/json",
        },
      }
    );

    if (!response.ok) {
      if (response.status === 404) {
        console.log(`Word not found in dictionary: ${normalizedWord}`);
        return null;
      }
      throw new Error(`Dictionary API error: ${response.status}`);
    }

    const data: DictionaryAPIResponse[] = await response.json();

    if (!data || data.length === 0) {
      return null;
    }

    // Use the first result
    const entry = data[0];

    // Extract phonetic and audio (prefer first available)
    const phonetic = entry.phonetic || entry.phonetics?.[0]?.text || undefined;
    const audioUrl = entry.phonetics?.find(p => p.audio)?.audio || undefined;

    // Convert meanings to our WordSense format
    const senses: WordSense[] = [];
    let senseIndex = 0;

    for (const meaning of entry.meanings) {
      for (const def of meaning.definitions) {
        const sense: WordSense = {
          id: `s${++senseIndex}`,
          pos: meaning.partOfSpeech,
          defEn: def.definition,
          examples: def.example ? [{ en: def.example }] : [],
          synonyms: Array.from(new Set([
            ...(def.synonyms || []),
            ...(meaning.synonyms || [])
          ])).slice(0, 10),  // Limit to 10
          antonyms: Array.from(new Set([
            ...(def.antonyms || []),
            ...(meaning.antonyms || [])
          ])).slice(0, 10),  // Limit to 10
        };

        senses.push(sense);
      }
    }

    // Build provider info
    const provider: ProviderInfo = {
      name: "dictionaryapi.dev",
      retrievedAt: Date.now(),
      licenseNote: "Free Dictionary API - Educational use only",
    };

    // Build complete word entry
    const wordEntry: WordEntry = {
      lemma: normalizedWord,
      headword: entry.word,
      phonetic,
      audioUrl,
      origin: entry.origin,
      senses,
      provider,
      enReady: true,
      zhReady: false,  // Chinese translation not yet generated
    };

    console.log(`✓ Fetched dictionary data for "${normalizedWord}" - ${senses.length} senses`);

    return wordEntry;
  } catch (error) {
    console.error(`Error fetching from dictionary API for "${normalizedWord}":`, error);
    throw error;
  }
}

/**
 * Extract collocation phrases from examples
 * Simple heuristic: look for common patterns like "verb + noun", "adj + noun"
 */
export function extractCollocations(word: string, examples: string[]): Array<{ phrase: string }> {
  const collocations: Array<{ phrase: string }> = [];
  const wordLower = word.toLowerCase();

  for (const example of examples) {
    const exampleLower = example.toLowerCase();

    // Find sentences containing the word
    if (exampleLower.includes(wordLower)) {
      // Extract 2-4 word phrases containing the target word
      const words = example.split(/\s+/);
      const wordIndex = words.findIndex(w => w.toLowerCase().includes(wordLower));

      if (wordIndex !== -1) {
        // Try to extract phrase around the word
        const start = Math.max(0, wordIndex - 2);
        const end = Math.min(words.length, wordIndex + 3);
        const phrase = words.slice(start, end).join(" ");

        if (phrase.length > word.length + 2 && phrase.length < 50) {
          collocations.push({ phrase: phrase.replace(/[.,!?;:]$/g, "") });
        }
      }
    }
  }

  // Return unique collocations (max 5)
  return Array.from(new Set(collocations.map(c => c.phrase)))
    .slice(0, 5)
    .map(phrase => ({ phrase }));
}

server/translator.ts.content:
/**
 * AI Translator Service
 * 
 * Strictly translates English dictionary content to Traditional Chinese (Taiwan).
 * Does NOT create new content - only translates what the dictionary provides.
 * 
 * Key principles:
 * - Translate faithfully from source
 * - Use Taiwan-style Chinese
 * - Keep definitions concise
 * - Don't add new examples or senses
 */

import OpenAI from "openai";
import { type WordSense } from "@shared/schema";

const openai = new OpenAI({
  baseURL: process.env.AI_INTEGRATIONS_OPENAI_BASE_URL,
  apiKey: process.env.AI_INTEGRATIONS_OPENAI_API_KEY,
});

/**
 * Translate word senses to Traditional Chinese
 * 
 * Input: Array of senses with English content
 * Output: Array of partial senses with Chinese translations
 */
export async function translateWordSenses(
  headword: string,
  senses: any[]
): Promise<Partial<WordSense>[]> {
  try {
    // Prepare translation payload (English only)
    const translationInput = senses.map((sense, index) => ({
      id: sense.id || `s${index + 1}`,
      pos: sense.pos,
      defEn: sense.defEn,
      examples: (sense.examples || []).map((ex: any) => ex.en).filter(Boolean),
      collocations: (sense.collocations || []).map((coll: any) => coll.phrase).filter(Boolean),
    }));

    // System prompt - strict translation guidelines
    const systemPrompt = `你是專業的英→繁中（台灣）字典編輯。請「只翻譯」我提供的英文定義與例句，不要新增或杜撰任何義項。

要求：
1. 使用台灣繁體中文用詞（例：「電腦」而非「計算機」、「公司」而非「公司行號」）
2. **定義要極度簡潔，用於字卡學習**：
   - 常見具體名詞（如動物、物品、人物）：只寫對應中文詞彙（例：frog → 青蛙；dog → 狗；computer → 電腦）
   - 常見動詞：只寫對應中文動詞（例：run → 跑；eat → 吃；sleep → 睡覺）
   - 常見形容詞：只寫對應中文形容詞（例：happy → 快樂的；big → 大的）
   - 抽象概念或複雜詞彙：用 1-2 個短句說明，不超過 15 字（例：democracy → 民主政治；photosynthesis → 植物利用光合成養分）
3. 例句以直譯為主，必要時意譯使其自然、簡短
4. 詞組搭配（collocations）翻成常用說法
5. 不要加入你自己的新例句或新義項
6. 詞性保持英文原樣（verb, noun, adjective 等）
7. 如果某個欄位沒有內容，留空或返回 null

輸出必須符合 JSON schema，不要有多餘文字。`;

    const userPrompt = `請翻譯單字 "${headword}" 的以下義項：

${JSON.stringify(translationInput, null, 2)}

返回 JSON 格式：
{
  "translations": [
    {
      "id": "s1",
      "defZhTw": "中文定義",
      "examplesZhTw": ["中文例句1", "中文例句2"],
      "collocationsZhTw": ["詞組翻譯1", "詞組翻譯2"],
      "notesZhTw": "（選填）使用說明"
    }
  ]
}`;

    // Call GPT-4o with strict JSON mode
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        { role: "system", content: systemPrompt },
        { role: "user", content: userPrompt },
      ],
      response_format: { type: "json_object" },
      max_completion_tokens: 2000,
      temperature: 0.3,  // Lower temperature for more consistent translation
    });

    const content = response.choices[0]?.message?.content;

    if (!content) {
      throw new Error("Empty response from AI");
    }

    const parsed = JSON.parse(content);
    const translations = parsed.translations || [];

    // Validate and map back to WordSense structure
    const result: Partial<WordSense>[] = translations.map((t: any, index: number) => ({
      id: t.id || senses[index]?.id,
      defZhTw: t.defZhTw || undefined,
      examples: (t.examplesZhTw || []).map((zhTw: string, exIndex: number) => ({
        en: senses[index]?.examples?.[exIndex]?.en || "",
        zhTw,
      })),
      collocations: (t.collocationsZhTw || []).map((zhTw: string, collIndex: number) => ({
        phrase: senses[index]?.collocations?.[collIndex]?.phrase || "",
        zhTw,
      })),
      notesZhTw: t.notesZhTw || undefined,
    }));

    console.log(`✓ Translated ${result.length} senses for "${headword}"`);

    return result;

  } catch (error) {
    console.error(`Translation error for "${headword}":`, error);
    throw new Error(`Failed to translate word senses: ${error instanceof Error ? error.message : "Unknown error"}`);
  }
}

/**
 * Generate batch Chinese definitions for flashcards (legacy support)
 * This is a simplified version for when we don't have full dictionary data
 */
export async function generateBatchDefinitions(
  words: string[]
): Promise<Array<{ word: string; definition: string; partOfSpeech: string }>> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `你是英中雙語詞彙專家。提供準確的台灣繁體中文定義。`,
        },
        {
          role: "user",
          content: `為以下英文單字提供台灣繁體中文定義：${words.join(", ")}

對每個單字返回：
- "word": 原始英文單字
- "definition": 簡潔的繁體中文定義（最多20字）
- "partOfSpeech": 詞性（名詞、動詞、形容詞、副詞等）

重要：定義保持簡潔（最多20個繁體中文字）。

返回 JSON：
{
  "definitions": [
    {
      "word": "happy",
      "definition": "快樂的；高興的",
      "partOfSpeech": "形容詞"
    }
  ]
}`,
        },
      ],
      response_format: { type: "json_object" },
      max_completion_tokens: 1000,
    });

    const content = response.choices[0]?.message?.content || "{}";
    const parsed = JSON.parse(content);
    const rawDefinitions = parsed.definitions || [];

    // Validate and sanitize
    const validDefinitions = rawDefinitions
      .filter((def: any) => def.word && def.definition && def.partOfSpeech)
      .map((def: any) => ({
        word: def.word,
        definition: def.definition.length > 20 
          ? def.definition.substring(0, 20)
          : def.definition,
        partOfSpeech: def.partOfSpeech || "未知",
      }));

    console.log(`Generated ${validDefinitions.length} definitions out of ${words.length} words`);

    return validDefinitions;
  } catch (error) {
    console.error("Error generating batch definitions:", error);
    throw new Error("Failed to generate batch definitions");
  }
}

